import { useState } from "react";
import {
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword,
    onAuthStateChanged,
    signOut,
} from "firebase/auth";
import { auth } from "./firebaseConfig";

function Register() {
    const [registerEmail, setRegisterEmail] = useState("");
    const [registerPassword, setRegisterPassword] = useState("");
    const [loginEmail, setLoginEmail] = useState("");
    const [loginPassword, setLoginPassword] = useState("");

    const [user, setUser] = useState({});

    onAuthStateChanged(auth, (currentUser) => {
        setUser(currentUser);
    });

    const handleRegister = async (event) => { // Add an event parameter to the register function
        event.preventDefault(); // Prevent the default form submission behavior
        try {
            const user = await createUserWithEmailAndPassword(
                auth,
                registerEmail,
                registerPassword
            );
            console.log(user);
        } catch (error) {
            console.log(error.message);
        }
    };

    const handleLogin = async (event) => { // Add an event parameter to the login function
        event.preventDefault(); // Prevent the default form submission behavior
        try {
            const user = await signInWithEmailAndPassword(
                auth,
                loginEmail,
                loginPassword
            );
            console.log(user);
        } catch (error) {
            console.log(error.message);
        }
    };

    const handleLogout = async () => { // Remove the event parameter from the logout function
        await signOut(auth);
    };

    return (
        <div className="main">
            <div className="container">
                <form id="form" name="form" onSubmit={handleRegister}> {/* Change the onSubmit function to handleRegister */}
                    <h2>Sign In</h2>

                    <label htmlFor="fname">First Name</label>
                    <input id="fname" type="text" name="fname" required/>

                    <label htmlFor="lname">Last Name</label>
                    <input id="lname" type="text" name="lname" required/>

                    <label htmlFor="email">Email</label>
                    <input
                        id="email"
                        type="email"
                        name="email"
                        value={registerEmail}
                        onChange={(event) => {
                            setRegisterEmail(event.target.value);
                        }}
                        required
                    />

                    {/*<label htmlFor="password">Password</label>*/}
                    {/*<input*/}
                    {/*    id="password"*/}
                    {/*    type="password"*/}
                    {/*    name="password"*/}
                    {/*    value={registerPassword}*/}
                    {/*    onChange={(event) => {*/}
                    {/*        setRegisterPassword(event.target.value);*/}
                    {/*    }}*/}
                    {/*    required*/}
                    {/*/>*/}

                    <input id="sub-btn" type="submit" name="submit" value="Sign In"/>
                    <p>
                        Already registered? <a href="login.html">Log in here</a>.
                    </p>
                </form>
            </div>
        </div>
    );
}

export default Register;
