import React, {useState} from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import {  createUserWithEmailAndPassword  } from 'firebase/auth';
import { auth } from "./firebaseConfig";
import './Register.css';

const Register = () => {
    const navigate = useNavigate();

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('');

    const onSubmit = async (e) => {
        e.preventDefault()

        await createUserWithEmailAndPassword(auth, email, password)
            .then((userCredential) => {

                const user = userCredential.user;
                console.log(user);
                alert("Registered Successfully")
                navigate("/login")

            })
            .catch((error) => {
                const errorCode = error.code;
                const errorMessage = error.message;
                alert(errorMessage)
                console.log(errorCode, errorMessage);

            });




    }

    return (
        <main >
            <section>
                <div>
                    <div className="main">
                        <div className="container">
                            <form style={{ width: '100%', maxWidth: '400px', padding: '20px', backgroundColor: '#f2f2f2', borderRadius: '5px' }}>
                                <div>
                                    <label htmlFor="email-address">Email address</label>
                                    <input type="email" label="Email address" value={email} onChange={(e) => setEmail(e.target.value)} required placeholder="Email address" style={{ width: '100%', padding: '10px', marginBottom: '20px', borderRadius: '5px', border: 'none' }} />
                                </div>

                                <div>
                                    <label htmlFor="password">Password</label>
                                    <input type="password" label="Create password" value={password} onChange={(e) => setPassword(e.target.value)} required placeholder="Password" style={{ width: '100%', padding: '10px', marginBottom: '20px', borderRadius: '5px', border: 'none' }} />
                                </div>

                                <button type="submit" onClick={onSubmit} style={{ backgroundColor: '#4CAF50', color: '#fff', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Sign up</button>

                                <p>
                                    Already have an account?{' '}
                                    <NavLink to="/login" style={{ color: '#4CAF50' }}>
                                        Sign in
                                    </NavLink>
                                </p>
                            </form>

                        <p>
                            Already have an account?{' '}
                            <NavLink to="/login" >
                                Sign in
                            </NavLink>
                        </p>
                    </div>
                </div>
                </div>
            </section>
        </main>
    )
}

export default Register
