import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ContactTable = () => {
    const [contacts, setContacts] = useState([]);

    useEffect(() => {
        axios.get('http://localhost/reactfirebase/Php/Read.php')
            .then((res) => {
                setContacts(res.data);
            })
            .catch((err) => {
                console.error(err);
                alert('Error fetching data');
            });
    }, []);

    const handleDelete = (id) => {
        axios.post('http://localhost/reactfirebase/Php/Delete.php', { id })
            .then((res) => {
                console.log(res.data);
                alert(res.data);
                setContacts((prevContacts) => prevContacts.filter((contact) => contact.id !== id));
            })
            .catch((err) => {
                console.error(err);
                alert('Error deleting data');
            });
    };

    const handleEdit = (contact) => {
        setEditingContact(contact);
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();

        const { name, phone, address, email } = event.target.elements;
        const updatedContact = {
            id: editingContact.id,
            name: name.value,
            phone: phone.value,
            address: address.value,
            email: email.value,
        };

        axios.post('http://localhost/reactfirebase/Php/Update.php', updatedContact)
            .then((res) => {
                console.log(res.data);
                alert(res.data);
                setContacts((prevContacts) => {
                    const updatedContacts = [...prevContacts];
                    const index = updatedContacts.findIndex((contact) => contact.id === updatedContact.id);
                    updatedContacts[index] = updatedContact;
                    return updatedContacts;
                });
                setEditingContact(null);
            })
            .catch((err) => {
                console.error(err);
                alert('Error updating data');
            });
    };

    return (
        <table>
            <thead>
            <tr>
                <th>Name</th>
                <th>Phone</th>
                <th>Address</th>
                <th>Email</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {contacts.map((contact) => (
                <tr key={contact.id}>
                    <td>{contact.name}</td>
                    <td>{contact.phone}</td>
                    <td>{contact.address}</td>
                    <td>{contact.email}</td>
                    <td>
                        <button onClick={() => handleDelete(contact.id)}>Delete</button>
                        <button>Edit</button>
                    </td>
                </tr>
            ))}
            </tbody>
        </table>
    );
};

export default ContactTable;
