import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ContactForm.css';

const ContactForm = ({ location }) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        address: '',
        email: ''
    });
    const [isEdit, setIsEdit] = useState(false);

    useEffect(() => {
        if (id) {
            axios.get(`http://localhost/reactfirebase/Php/ReadOne.php?id=${id}`)
                .then((res) => {
                    setContact(res.data);
                    setIsEdit(true);
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error fetching data');
                });
        }
    }, [id]);

    return (
        <div>
            <ContactForm
                formData={contact}
                setFormData={setContact}
                handleSubmit={handleSubmit}
                isEdit={isEdit}
            />
        </div>
    );

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = new URLSearchParams();
        data.append('name', contact.name);
        data.append('phone', contact.phone);
        data.append('address', contact.address);
        data.append('email', contact.email);

        if (isEdit) {
            axios.post(`http://localhost/reactfirebase/Php/Update.php?id=${id}`, data)
                .then((res) => {
                    console.log(res.data);
                    alert(res.data);
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error updating data');
                });
        } else {
            axios.post('http://localhost/reactfirebase/Php/Create.php', data)
                .then((res) => {
                    console.log(res.data);
                    alert(res.data);
                    setContact({ name: '', phone: '', address: '', email: '' });
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error storing data');
                });
        }
    };


    return (
        <div className="form">
            <div className="form-container">
                <h2>{isEdit ? 'Edit Contact' : 'Add Contact'}</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Name:</label>
                        <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="phone">Phone:</label>
                        <input type="text" id="phone" name="phone" value={formData.phone} onChange={handleChange} required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="address">Address:</label>
                        <input type="text" id="address" name="address" value={formData.address} onChange={handleChange} required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">Email:</label>
                        <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} required />
                    </div>

                    <button type = "submit">{isEdit ? 'Update' : 'Add'}</button>
                </form>
            </div>
        </div>
    );
};

export default ContactForm;
