import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ContactForm from './ContactForm';
import ContactTable from '../Component/View';
import '/Home.css';

const Home = () => {
    const [showForm, setShowForm] = useState(false);
    const [contacts, setContacts] = useState([]);

    useEffect(() => {
        axios.get('http://localhost/reactfirebase/Php/Read.php')
            .then((res) => {
                setContacts(res.data);
            })
            .catch((err) => {
                console.error(err);
                alert('Error fetching data');
            });
    }, []);

    const handleCreateClick = () => {
        setShowForm(true);
    };

    const handleFormClose = () => {
        setShowForm(false);
    };

    const handleCreate = (newContact) => {
        setContacts([...contacts, newContact]);
    };

    return (
        <main>
            <section>
                <div>
                    <h1>Contact List</h1>
                    <button onClick={handleCreateClick}>Create New</button>
                    {showForm && <ContactForm onCreate={handleCreate} onClose={handleFormClose} />}
                    <ContactTable contacts={contacts} />
                </div>
            </section>
        </main>
    );
};

export default Home;
