import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ContactForm.css';

const ContactForm = ({ location }) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        address: '',
        email: ''
    });
    const [isEdit, setIsEdit] = useState(false);

    useEffect(() => {
        const searchParams = new URLSearchParams(location.search);
        const id = searchParams.get('id');
        if (id) {
            axios.get(`http://localhost/reactfirebase/Php/Read.php?id=${id}`)
                .then((res) => {
                    setFormData(res.data);
                    setIsEdit(true);
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error fetching data');
                });
        }
    }, [location.search]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = new URLSearchParams();
        data.append('name', formData.name);
        data.append('phone', formData.phone);
        data.append('address', formData.address);
        data.append('email', formData.email);

        if (isEdit) {
            const searchParams = new URLSearchParams(location.search);
            const id = searchParams.get('id');
            axios.put(`http://localhost/reactfirebase/Php/Update.php?id=${id}`, data)
                .then((res) => {
                    console.log(res.data);
                    alert(res.data);
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error updating data');
                });
        } else {
            axios.post('http://localhost/reactfirebase/Php/Create.php', data)
                .then((res) => {
                    console.log(res.data);
                    alert(res.data);
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error storing data');
                });
        }
        setFormData({ name: '', phone: '', address: '', email: '' });
    };

    return (
        <div className="form">
            <div className="form-container">
                <h2>{isEdit ? 'Edit Contact' : 'Add Contact'}</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Name:</label>
                        <input type="text" id="name" name="name" value={formData.name} onChange={handleChange} required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="phone">Phone:</label>
                        <input type="text" id="phone" name="phone" value={formData.phone} onChange={handleChange} required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="address">Address:</label>
                        <input type="text" id="address" name="address" value={formData.address} onChange={handleChange} required />
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">Email:</label>
                        <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} required />
                    </div>

                    <button type = "submit">{isEdit ? 'Update' : 'Add'}</button>
                </form>
            </div>
        </div>
    );
};

export default ContactForm;
