import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ContactForm = (props) => {
    const navigate = useNavigate();

    const [contact, setContact] = useState({
        name: '',
        phone: '',
        address: '',
        email: '',
    });

    const [isEdit, setIsEdit] = useState(false);

    useEffect(() => {
        if (props.location.state && props.location.state.contact) {
            setContact(props.location.state.contact);
            setIsEdit(true);
        }
    }, [props.location.state]);

    const handleChange = (e) => {
        setContact({ ...contact, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (isEdit) {
            axios
                .post('http://localhost/reactfirebase/Php/Update.php', contact)
                .then((res) => {
                    console.log(res.data);
                    alert(res.data);
                    navigate('/');
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error updating data');
                });
        } else {
            axios
                .post('http://localhost/reactfirebase/Php/Create.php', contact)
                .then((res) => {
                    console.log(res.data);
                    alert(res.data);
                    navigate('/');
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error creating data');
                });
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" value={contact.name} onChange={handleChange} />
                </div>
                <div>
                    <label>Phone:</label>
                    <input type="text" name="phone" value={contact.phone} onChange={handleChange} />
                </div>
                <div>
                    <label>Address:</label>
                    <input type="text" name="address" value={contact.address} onChange={handleChange} />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="text" name="email" value={contact.email} onChange={handleChange} />
                </div>
                <button type="submit">{isEdit ? 'Update' : 'Create'}</button>
            </form>
        </div>
    );
};

export default ContactForm;
