import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ContactForm = (props) => {
    const navigate = useNavigate();
    const [name, setName] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [email, setEmail] = useState('');
    const [isEdit, setIsEdit] = useState(false);
    const [contact, setContact] = useState(null);

    useEffect(() => {
        if (props.location.state && props.location.state.contact) {
            setContact(props.location.state.contact);
            setIsEdit(true);
            setName(props.location.state.contact.name);
            setPhone(props.location.state.contact.phone);
            setAddress(props.location.state.contact.address);
            setEmail(props.location.state.contact.email);
        }
    }, [props.location.state]);

    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('name', name);
        formData.append('phone', phone);
        formData.append('address', address);
        formData.append('email', email);
        if (isEdit) {
            formData.append('id', contact.id);
            axios.post('http://localhost/reactfirebase/Php/Update.php', formData)
                .then((res) => {
                    console.log(res.data);
                    alert(res.data);
                    navigate('/');
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error updating data');
                });
        } else {
            axios.post('http://localhost/reactfirebase/Php/Create.php', formData)
                .then((res) => {
                    console.log(res.data);
                    alert(res.data);
                    navigate('/');
                })
                .catch((err) => {
                    console.error(err);
                    alert('Error creating data');
                });
        }
    };

    return (
        <div>
            <h2>{isEdit ? 'Update Contact' : 'Create Contact'}</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" value={name} onChange={(event) => setName(event.target.value)} />
                </div>
                <div>
                    <label htmlFor="phone">Phone:</label>
                    <input type="text" id="phone" value={phone} onChange={(event) => setPhone(event.target.value)} />
                </div>
                <div>
                    <label htmlFor="address">Address:</label>
                    <input type="text" id="address" value={address} onChange={(event) => setAddress(event.target.value)} />
                </div>
                <div>
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" value={email} onChange={(event) => setEmail(event.target.value)} />
                </div>
                <button type="submit">{isEdit ? 'Update' : 'Create'}</button>
            </form>
        </div>
    );
};

export default ContactForm;
