import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'
import axios from 'axios';

const ContactTable = () => {
    const [contacts, setContacts] = useState([]);
    const navigate = useNavigate();
    useEffect(() => {
        axios.get('http://localhost/reactfirebase/Php/Read.php')
            .then((res) => {
                setContacts(res.data);
            })
            .catch((err) => {
                console.error(err);
                alert('Error fetching data');
            });
    }, []);

    const handleDelete = (id) => {
        axios.post('http://localhost/reactfirebase/Php/Delete.php', { id })
            .then((res) => {
                console.log(res.data);
                alert(res.data);
                setContacts((prevContacts) => prevContacts.filter((contact) => contact.id !== id));
            })
            .catch((err) => {
                console.error(err);
                alert('Error deleting data');
            });
    };

    const handleEdit = (id) => {
        // Redirect to the Create page with the contact id as a query parameter and isEdit as true
        navigate(`/ContactForm?id=${id}`, { state: { isEdit: true } });
    };


    return (
        <table>
            <thead>
            <tr>
                <th>Name</th>
                <th>Phone</th>
                <th>Address</th>
                <th>Email</th>
                <th>Actions</th>
            </tr>
            </thead>
            <tbody>
            {contacts.map((contact) => (
                <tr key={contact.id}>
                    <td>{contact.name}</td>
                    <td>{contact.phone}</td>
                    <td>{contact.address}</td>
                    <td>{contact.email}</td>
                    <td>
                        <button onClick={() => handleDelete(contact.id)}>Delete</button>
                        <button onClick={() => handleEdit(contact.id)}>Edit</button>
                    </td>
                </tr>
            ))}
            </tbody>
        </table>
    );
};

export default ContactTable;
