import React from "react";
import firebase from "firebase/app";
import "firebase/auth";
import { firebaseConfig } from "./firebaseConfig";

class Register extends React.Component {
    componentDidMount() {
        firebase.initializeApp(firebaseConfig);
    }

    onSubmit = (event) => {
        event.preventDefault();

        const { fname, lname, email, password } = event.target.elements;

        firebase
            .auth()
            .createUserWithEmailAndPassword(email.value, password.value)
            .then((userCredential) => {
                // Signed in
                const user = userCredential.user;
                console.log(user);
            })
            .catch((error) => {
                const errorCode = error.code;
                const errorMessage = error.message;
                console.log(errorCode, errorMessage);
            });
    };

    render() {
        return (
            <div className="main">
                <div className="container">
                    <form id="form" name="form" onSubmit={this.onSubmit}>
                        <h2>Sign In</h2>

                        <label htmlFor="fname">First Name</label>
                        <input id="fname" type="text" name="fname" required />

                        <label htmlFor="lname">Last Name</label>
                        <input id="lname" type="text" id="lname" name="lname" required />

                        <label htmlFor="email">Email</label>
                        <input id="email" type="email" name="email" required />

                        <label htmlFor="password">Password</label>
                        <input id="password" type="password" id="password" name="password" required />

                        <input id="sub-btn" type="submit" name="submit" value="Sign In" />
                        <p>
                            Already registered? <a href="login.html">Log in here</a>.
                        </p>
                    </form>
                </div>
            </div>
        );
    }
}

export default Register;
